"use client";

import * as React from "react";
import {
  flexRender,
  getCoreRowModel,
  useReactTable,
} from "@tanstack/react-table";
import type { PaginationOptions, TableMeta } from "@tanstack/react-table";
import { RefreshCcwIcon } from "lucide-react";
import {
  Button,
  cn,
  Skeleton,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "uilab-core";
import type { DataColumn, DataPaginationState } from ".";
import Pagination from "./pagination";

type Props = {
  /**
   * Made to be fit with Pocket Pageable content response.
   */
  data: unknown[];
  /**
   * Collection of column definitions with
   * [Tanstack Table](https://tanstack.com/table/latest/docs/api/core/column-def) standard
   */
  columns: DataColumn<any>[];
  /**
   * Default column options to use for all column defs supplied to the table.
   */
  defaultColumn?: DataColumn<any> | undefined;
  /**
   * Component will render loader skeleton if `true`. Should be provided from `useQuery` from [Tanstack Query](https://tanstack.com/query/v4/docs/framework/react/reference/useQuery)
   */
  isFetching: boolean;
  /**
   * You have to define TableCell in your column for more complex use cases
   */
  isCustomCell?: boolean;
  /**
   * State of pagination to keep track. On tanstack table, `manualPagination` is `true`, so you can update the state whenever you call an api.
   */
  pagination?: DataPaginationState;
  paginationTotalText?: (total: string | number) => string;
  paginationPageText?: (
    current: string | number,
    total: string | number,
  ) => string;
  paginationRowsText?: (rows: string | number) => string;
  /**
   * Necessary for rendering `Pagination` component to keep track.
   */
  paginationOptions?: Pick<
    PaginationOptions,
    "onPaginationChange" | "rowCount" | "pageCount"
  >;
  /**
   * Refresh button text
   */
  refreshText?: string;
  /**
   * No results text
   */
  noResultsText?: string;
  /**
   * Element provided will be attached to the header's left side
   */
  leftHeader?: React.ReactNode;
  /**
   * Element provided will be attached to the header's right side
   */
  rightHeader?: React.ReactNode;
  /**
   * Additional data or method needed for the table
   */
  meta?: TableMeta<any>;
  /**
   * Border around the table
   */
  border?: boolean;
  /**
   * Optional if you want an action button that needs to reload api calls.
   * Requires `isFetching` field and `refetch` function
   * QOL component
   */
  refetch?: () => void;
};

/**
 * Advanced data tables that needs complex features such as Pagination, Filters, Column Hiding etc
 */
export function AdvancedDataTable({
  data,
  columns,
  defaultColumn,
  isFetching,
  isCustomCell,
  pagination,
  paginationTotalText,
  paginationPageText,
  paginationRowsText,
  paginationOptions = {},
  refreshText = "Refresh",
  noResultsText = "No results",
  leftHeader,
  rightHeader,
  border = true,
  meta,
  refetch,
}: Props) {
  const table = useReactTable({
    data: data || [],
    columns,
    defaultColumn,
    getCoreRowModel: getCoreRowModel(),
    manualPagination: true,
    ...paginationOptions,
    initialState: { pagination },
    meta,
  });

  const renderHeaders = React.useCallback(
    () =>
      table.getHeaderGroups().map((headerGroup) => (
        <TableRow key={headerGroup.id}>
          {headerGroup.headers.map((header) => (
            <TableHead
              className={header.id === "action" ? "text-right" : ""}
              key={header.id}
            >
              {header.isPlaceholder
                ? null
                : flexRender(
                    header.column.columnDef.header,
                    header.getContext(),
                  )}
            </TableHead>
          ))}
        </TableRow>
      )),
    [table],
  );

  return (
    <div className="flex w-full flex-col gap-4">
      <div className="flex w-full items-center justify-between">
        <div className="flex items-center gap-2">
          {leftHeader && leftHeader}
        </div>
        <div className="flex items-center justify-end gap-2">
          {rightHeader && rightHeader}
          {refetch && (
            <Button variant="outline" onClick={refetch} disabled={isFetching}>
              <RefreshCcwIcon /> {refreshText}
            </Button>
          )}
        </div>
      </div>
      {isFetching ? (
        <div className="flex w-full flex-col gap-2">
          <Skeleton className="h-7 w-full" />
          <Skeleton className="h-7 w-3/4" />
          <Skeleton className="h-7 w-1/2" />
        </div>
      ) : (
        <>
          <div className={cn("rounded-lg", border && "border")}>
            <Table>
              <TableHeader>{renderHeaders()}</TableHeader>
              <TableBody>
                {table.getRowModel().rows?.length ? (
                  table.getRowModel().rows.map((row) => (
                    <TableRow
                      key={row.id}
                      className={cn(
                        row.id === "actions" && "flex flex-row-reverse",
                      )}
                      data-state={row.getIsSelected() && "selected"}
                    >
                      {row.getVisibleCells().map((cell) => {
                        const render = flexRender(
                          cell.column.columnDef.cell,
                          cell.getContext(),
                        );

                        return isCustomCell ? (
                          render
                        ) : (
                          <TableCell
                            className={cn(
                              cell.column.id === "actions" &&
                                "flex flex-row-reverse",
                            )}
                            key={cell.id}
                          >
                            {render}
                          </TableCell>
                        );
                      })}
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell
                      colSpan={columns.length}
                      className="h-24 text-center"
                    >
                      {noResultsText}
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </div>
          {pagination && (
            <Pagination
              totalText={paginationTotalText}
              pageText={paginationPageText}
              rowsText={paginationRowsText}
              table={table}
              state={pagination}
            />
          )}
        </>
      )}
    </div>
  );
}

"use client";

import * as React from "react";
import {
  flexRender,
  getCoreRowModel,
  useReactTable,
  type TableMeta,
} from "@tanstack/react-table";
import {
  cn,
  Skeleton,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "uilab-core";
import type { DataColumn } from ".";

type Props = {
  /**
   * Content to display
   */
  data: Array<any>;
  /**
   * Collection of column definitions with
   * [Tanstack Table](https://tanstack.com/table/latest/docs/api/core/column-def) standard
   */
  columns: DataColumn<any>[];
  /**
   * Default column options to use for all column defs supplied to the table.
   */
  defaultColumn?: DataColumn<any> | undefined;
  /**
   * Border around the table
   */
  border?: boolean;
  /**
   * No Results text
   */
  noResultsText?: string;
  /**
   * Additional data or method needed for the table
   */
  meta?: TableMeta<any>;
  /**
   * Component will render loader skeleton if `true`. Should be provided from `useQuery` from [Tanstack Query](https://tanstack.com/query/v4/docs/framework/react/reference/useQuery)
   */
  isFetching?: boolean;
  /**
   * You have to define TableCell in your column for more complex use cases
   */
  isCustomCell?: boolean;
};

export function BasicDataTable({
  data,
  columns,
  defaultColumn,
  border = true,
  noResultsText = "No results",
  meta,
  isFetching,
  isCustomCell,
}: Props) {
  const table = useReactTable({
    data: data || [],
    columns,
    defaultColumn,
    meta,
    getCoreRowModel: getCoreRowModel(),
  });

  const renderHeaders = React.useCallback(
    () =>
      table.getHeaderGroups().map((headerGroup) => (
        <TableRow key={headerGroup.id}>
          {headerGroup.headers.map((header) => (
            <TableHead
              className={header.id === "action" ? "text-right" : ""}
              key={header.id}
            >
              {header.isPlaceholder
                ? null
                : flexRender(
                    header.column.columnDef.header,
                    header.getContext(),
                  )}
            </TableHead>
          ))}
        </TableRow>
      )),
    [table],
  );

  if (isFetching) {
    return (
      <div className="flex w-full flex-col gap-2">
        <Skeleton className="h-7 w-full" />
        <Skeleton className="h-7 w-3/4" />
        <Skeleton className="h-7 w-1/2" />
      </div>
    );
  }

  // Did not use `useReactTable` properties as it is broken
  return (
    <div className="w-full">
      <div className={cn("mb-2 rounded-lg", border && "border")}>
        <Table>
          <TableHeader>{renderHeaders()}</TableHeader>
          <TableBody>
            {table.getRowModel().rows?.length ? (
              table.getRowModel().rows.map((row) => (
                <TableRow
                  key={row.id}
                  data-state={row.getIsSelected() && "selected"}
                >
                  {row.getVisibleCells().map((cell) => {
                    const render = flexRender(
                      cell.column.columnDef.cell,
                      cell.getContext(),
                    );

                    return isCustomCell ? (
                      render
                    ) : (
                      <TableCell
                        className={cn(
                          cell.column.id === "actions" &&
                            "flex flex-row-reverse",
                        )}
                        key={cell.id}
                      >
                        {render}
                      </TableCell>
                    );
                  })}
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell
                  colSpan={columns.length}
                  className="h-24 text-center"
                >
                  {noResultsText}
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>
    </div>
  );
}

"use client";

import * as React from "react";
import { FilterIcon } from "lucide-react";
import { Button, Popover, PopoverContent, PopoverTrigger } from "uilab-core";

type Props = {
  /**
   * Number of filters that is currently applied
   * use `calculateAppliedFilters` function for ease of use
   */
  applied?: number;
  applyText?: string;
  clearText?: string;
  filterText?: string;
  filterAppliedText?: (applied: number) => string;
  children?: React.ReactNode;
  onSubmit?: () => Promise<void>;
  onReset?: () => any;
};

export function FilterPopover({
  applied,
  applyText = "Apply",
  clearText = "Clear",
  filterText = "Filter",
  filterAppliedText = (applied: number) => `${applied} filter applied`,
  children,
  onSubmit,
  onReset,
}: Props) {
  const [open, setOpen] = React.useState<boolean>(false);

  const handleSubmit = () => {
    if (onSubmit) onSubmit();
    setOpen(false);
  };

  const handleClear = () => {
    if (onReset) onReset();
    setOpen(false);
  };

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button variant={applied !== 0 ? "secondary" : "outline"}>
          <FilterIcon />
          {applied !== 0 ? filterAppliedText(applied) : filterText}
        </Button>
      </PopoverTrigger>
      <PopoverContent align="start" className="w-xs space-y-3">
        <div className="grid w-full grid-cols-1 gap-3">{children}</div>
        <div className="flex flex-row-reverse gap-2">
          <Button type="button" onClick={handleSubmit}>
            {applyText}
          </Button>
          <Button type="button" variant="outline" onClick={handleClear}>
            {clearText}
          </Button>
        </div>
      </PopoverContent>
    </Popover>
  );
}

"use client";

import * as React from "react";
import { CellContext } from "@tanstack/react-table";
import { cn, TableCell } from "uilab-core";

type Props = {
  context: CellContext<any, unknown>;
  border?: boolean;
} & React.ComponentPropsWithoutRef<"div">;

/**
 * In order to use this, you have to set `isCustomCell` to `true` in any form of table block component
 */
export function MergedCell({
  context,
  className,
  children,
  border = true,
}: Props) {
  const { row, table, column } = context;
  const cell = row.original[column.id];
  const rows = table.getRowModel().rows;
  const rowIndex = row.index;

  // Count how many rows have the same category starting from this row
  let rowSpan = 1;
  if (rowIndex === 0 || rows[rowIndex - 1].original[column.id] !== cell) {
    for (let i = rowIndex + 1; i < rows.length; i++) {
      if (rows[i].original[column.id] === cell) {
        rowSpan++;
      } else {
        break;
      }
    }

    return (
      <TableCell
        rowSpan={rowSpan}
        className={cn(className, border && "border")}
      >
        {children}
      </TableCell>
    );
  }

  return null;
}

"use client";

import * as React from "react";
import { ChevronDownIcon } from "lucide-react";
import {
  Button,
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
  DropdownMenuTrigger,
  Pagination,
  PaginationContent,
  PaginationNext,
  PaginationPrevious,
} from "uilab-core";
import type { DataPaginationState, DataTable } from ".";

type Props = {
  table: DataTable<unknown>;
  state: DataPaginationState;
  totalText?: (total: string | number) => string;
  pageText?: (current: string | number, total: string | number) => string;
  rowsText?: (rows: string | number) => string;
};

export default React.memo(function TablePagination({
  table,
  state,
  totalText = (total) => `Total of ${total} rows`,
  pageText = (current, total) => `Page ${current}/${total}`,
  rowsText = (rows) => `${rows} rows`,
}: Props) {
  // Tanstack methods are acting weird. I am actively working on it but for now this will do
  const canPreviousPage = React.useCallback(
    (): boolean => state.pageIndex > 0,
    [state],
  );
  const canNextPage = React.useCallback(
    (): boolean => state.pageIndex < table.getPageCount() - 1,
    [state, table],
  );

  return (
    <Pagination>
      <PaginationContent className="w-full justify-between gap-6">
        <p className="text-muted-foreground text-sm">
          {totalText(table.getRowCount() || "")}
        </p>
        <div className="flex justify-center gap-6">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="outline">
                {rowsText(state.pageSize)}
                <ChevronDownIcon className="stroke-muted-foreground" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent>
              <DropdownMenuRadioGroup
                value={state.pageSize.toString()}
                onValueChange={(value: string) =>
                  table.setPageSize(parseInt(value) || 20)
                }
              >
                <DropdownMenuRadioItem value="10">
                  {rowsText(10)}
                </DropdownMenuRadioItem>
                <DropdownMenuRadioItem value="25">
                  {rowsText(25)}
                </DropdownMenuRadioItem>
                <DropdownMenuRadioItem value="50">
                  {rowsText(50)}
                </DropdownMenuRadioItem>
              </DropdownMenuRadioGroup>
            </DropdownMenuContent>
          </DropdownMenu>
          <div className="flex items-center justify-center gap-2 text-sm">
            <span>
              {pageText(
                state.pageIndex + 1,
                (table.getPageCount() || "0").toLocaleString(),
              )}
            </span>
          </div>
          <div className="flex items-center gap-1 text-sm">
            <PaginationPrevious
              size="icon"
              onClick={() => table.previousPage()}
              isActive={canPreviousPage()}
            />
            <PaginationNext
              size="icon"
              onClick={() => table.nextPage()}
              isActive={canNextPage()}
            />
          </div>
        </div>
      </PaginationContent>
    </Pagination>
  );
});

import type {
  ColumnDef as TanColumnDef,
  PaginationState as TanPaginationState,
  Table as TanTable,
} from "@tanstack/react-table";

export type DataTable<T> = TanTable<T>;
export type DataColumn<T = unknown> = TanColumnDef<T>;
export type DataPaginationState = TanPaginationState;

export * from "./advanced";
export * from "./basic";
export * from "./filter-popover";
export * from "./merged-cell";
